mapper.select:


     and supplier_code = #{supplierCode}
     and supplier_name like concat('%', #{supplierName}, '%')
     and quality_incident = #{qualityIncident}
     and first_inspection_passRate = #{firstInspectionPassrate}
     and zero_kilometer_failureRate = #{zeroKilometerFailurerate}
     and quality_notification_orderRate = #{qualityNotificationOrderrate}
     and feedback_orderLetter_timeliness = #{feedbackOrderletterTimeliness}
     and warrantyPeriod_repairRate = #{warrantyperiodRepairrate}
     and threePackage_component_repairRate = #{threepackageComponentRepairrate}
     and secondParty_auditScore = #{secondpartyAuditscore}
     and selfInspection_reportAccuracy = #{selfinspectionReportaccuracy}
     and productMaterial_parameterSize_Change = #{productmaterialParametersizeChange}
     and weight = #{weight}
     and business_risk = #{businessRisk}
     and supply_guarantee = #{supplyGuarantee}
     and price_competitiveness = #{priceCompetitiveness}
     and costReduction_support = #{costreductionSupport}
     and service_and_coordination = #{serviceAndCoordination}
     and price_integrity = #{priceIntegrity}
     and payment_restrictionConditions = #{paymentRestrictionconditions}
     and newProduct_development_cooperationDegree = #{newproductDevelopmentCooperationdegree}
     and productTechnologyProblem_improvementTimeliness = #{producttechnologyproblemImprovementtimeliness}
     and total_score = #{totalScore}
     and happen_time = #{happenTime}
     and end_time = #{endTime}



     and storage_flag = #{storageFlag}

order by storage_flag desc,id asc
